package adt;/** * @author lee xue bao, Jia Hao */public interface ListInterface<T> {    public void add(T newEntry);    public boolean addObject(T newObject);    public boolean add(int newPosition, T newEntry);    public void remove(int givenPosition);    public boolean remove(T anObject);    public void clear();    public boolean replace(int givenPosition, T newEntry);    public boolean replace(T anObject, T newObject);    public T getObject(int positionIndex);    public T getEntry(int givenPosition);    public boolean contains(T anEntry);    public int getNumberOfEntries();    public int totalNumberOfObject();    public boolean isEmpty();    public boolean isListEmpty();    public boolean isListFull();}